import React from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { Grid } from "@mui/material";

function MovieCard(props) {
  console.log("props:", props);

  return (
    <>
      {props.movies.map((movie) => (
        <Grid item xs={12} md={6} lg={4}>
          <Card key={movie.id} sx={{ maxWidth: 345 }} style={{ borderRadius: "1rem" }}>
            <CardMedia
              component="img"
              height="140"
              image={`http://image.tmdb.org/t/p/w500/${movie.poster_path}`}
              alt="movie poster"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div" style={{ whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis" }}>
                {movie.title}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Rating: {movie.popularity}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">Share</Button>
              <Button size="small">Learn More</Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </>
  );
}

export default MovieCard;
