import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import { Typography, Toolbar, Container, Grid } from "@mui/material";
import MovieCard from "./MovieCard";

export default function Home() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);

  const loadMoreMovies = () => {
    setLoading(true);
    axios
      .get(
        `http://api.themoviedb.org/3/discover/movie?api_key=328c283cd27bd1877d9080ccb1604c91&sort_by=release_date.desc&original_title.desc&include_adult=false&page=${page}&primary_release_year=2021&language=en-US&region=us`
      )
      .then((res) => {
        const movies = res.data.results;

        movies.forEach((movie, index) => {
          setMovies((prev) => [...prev, movie]);
        });

        setLoading(false);
      });
  };

  const handlePage = () => {
    setPage((prev) => prev + 1);
  };

  useEffect(() => {
    loadMoreMovies();
  }, [page]);

  return (
    <div style={{ background: "black" }}>
      <Navbar />
      <Toolbar />
      <Container>
        <Typography
          variant="h5"
          style={{ color: "#ffffff", margin: "1rem 0", textAlign: "center" }}
        >
          Movie Listings
        </Typography>
        <Grid container spacing={3}>
          <MovieCard
            movies={movies}
            handlePage={handlePage}
            loadMoreMovies={loadMoreMovies}
          />
          {loading && (
            <Typography variant="h6" style={{ color: "#ffffff" }}>
              loading...
            </Typography>
          )}
        </Grid>
      </Container>
    </div>
  );
}
