import React, { useState, useEffect } from "react";
import {
  Typography,
  Toolbar,
  Container,
  FormControl,
  MenuItem,
  Select,
  InputLabel,
  Button,
} from "@mui/material";
import Navbar from "./Navbar";
import { useLocation } from "react-router-dom";
import PlaceholdePoster from "../assets/placeholder-poster.jpeg";
import axios from "axios";
import { UserAuth } from "../context/AuthContext";
import { db } from "../api/firebase";
import {
  arrayUnion,
  updateDoc,
  doc,
} from "firebase/firestore";

export default function MoviePage() {
  const location = useLocation();
  const [movie, setMovie] = useState({});
  const [genres, setGenres] = useState([]);
  const [format, setFormat] = useState("");
  const [cinema, setCinema] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [spokenLanguages, setSpokenLanguages] = useState([]);
  const { user } = UserAuth();

  useEffect(() => {
    axios
      .get(
        `http://api.themoviedb.org/3/movie/${location.state.movie.id}?api_key=328c283cd27bd1877d9080ccb1604c91`
      )
      .then((res) => {
        const movie = res.data;
        setMovie(movie);
        setGenres(movie.genres);
        setSpokenLanguages(movie.spoken_languages);
      });
  }, []);

  console.log("movie:", movie)

  const handleClick = () => {
    console.log("clicked")
    // const movieId = movie.id;
    // const userId = user.uid;
    // const bookingsCollectionRef = doc(db, "bookings", userId);

    // const createBooking = async () => {
    //   await updateDoc(
    //     bookingsCollectionRef,
    //     {
    //       user_bookings: arrayUnion({
    //         movie_id: movieId,
    //         cinema: cinema,
    //         format: format,
    //         date: date,
    //         time: time,
    //       }),
    //     },
    //     { merge: true }
    //   );
    // };

    // createBooking();
  };

  return (
    <div style={{ background: "hsl(40, 10%, 94%)" }}>
      <Navbar />
      <Toolbar />
      <Container>
        <img
          style={{ width: "100%", height: "auto", marginTop: "1rem" }}
          src={
            movie.poster_path
              ? `http://image.tmdb.org/t/p/w500/${movie.poster_path}`
              : PlaceholdePoster
          }
          alt="movie-poster"
        />
        <Typography
          style={{ fontWeight: "bold" }}
          mt={2}
          color="primary"
          gutterBottom
          variant="body2"
        >
          Synopsis
        </Typography>
        <Typography variant="caption">{movie.overview}</Typography>
        <div
          style={{
            padding: "0.75rem",
            background: "#fcf0f5",
            display: "flex",
            justifyContent: "space-around",
            margin: "1rem 0",
          }}
        >
          <div style={{ display: "flex", flexDirection: "column" }}>
            <Typography
              style={{ fontWeight: "bold" }}
              mt={2}
              color="primary"
              gutterBottom
              variant="body2"
            >
              Genre
            </Typography>
            {genres.map((genre) => (
              <Typography key={genre.id} variant="caption">
                {genre.name}
              </Typography>
            ))}
          </div>
          <div style={{ display: "flex", flexDirection: "column" }}>
            <Typography
              style={{ fontWeight: "bold" }}
              mt={2}
              color="primary"
              gutterBottom
              variant="body2"
            >
              Language
            </Typography>
            {spokenLanguages.map((spokenLanguage, index) => (
              <Typography key={index} variant="caption">
                {spokenLanguage.english_name}
              </Typography>
            ))}
          </div>
          <div>
            <Typography
              style={{ fontWeight: "bold" }}
              mt={2}
              color="primary"
              gutterBottom
              variant="body2"
            >
              Runtime
            </Typography>
            <Typography variant="caption">{movie.runtime} mins</Typography>
          </div>
        </div>
        <div>
          <FormControl style={{ width: "100%", color: "black" }} size="small">
            <InputLabel>Cinema</InputLabel>
            <Select label="Cinema" value={""}>
              <MenuItem>Cathay Cineplex Cineleisure</MenuItem>
              <MenuItem>Cathay Cineplex Causeway Point</MenuItem>
              <MenuItem>Cathay Cineplex Downtown East</MenuItem>
            </Select>
          </FormControl>
          <FormControl style={{ width: "100%", color: "black" }} size="small">
            <InputLabel>Format</InputLabel>
            <Select label="Format" value={""}>
              <MenuItem>Dolby Digital</MenuItem>
              <MenuItem>IMAX</MenuItem>
              <MenuItem>4DX</MenuItem>
            </Select>
          </FormControl>
          <FormControl style={{ width: "100%", color: "black" }} size="small">
            <InputLabel>Date</InputLabel>
            <Select label="Date" value={""}>
              <MenuItem>18 Jul 2022</MenuItem>
              <MenuItem>19 Jul 2022</MenuItem>
              <MenuItem>20 Jul 2022</MenuItem>
            </Select>
          </FormControl>
          <FormControl style={{ width: "100%", color: "black" }} size="small">
            <InputLabel>Time</InputLabel>
            <Select label="Time" value={""}>
              <MenuItem>13:00</MenuItem>
              <MenuItem>17:30</MenuItem>
              <MenuItem>19:30</MenuItem>
            </Select>
          </FormControl>
          <Button disabled={user ? false : true} onClick={handleClick()} variant="outlined">
            Book Now
          </Button>
        </div>
      </Container>
    </div>
  );
}
