import React, { useState, useEffect, createContext } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import { Typography, Toolbar, Container, Grid } from "@mui/material";
import MovieCard from "./MovieCard";

export default function Home() {
  const [movies, setMovies] = useState([]);
  const DialogContext = createContext() 

  useEffect(() => {
    axios
      .get(
        `http://api.themoviedb.org/3/discover/movie?api_key=328c283cd27bd1877d9080ccb1604c91&primary_release_date.lte=2016-12-31&sort_by=release_date.desc&page=2`
      )
      .then((res) => {
        // console.log("res:", res);
        const movies = res.data.results;
        setMovies(movies);
      });
  }, []);

  return (
    <div style={{ background: "black" }}>
      <Navbar />
      <Toolbar />
      <Container>
        <Typography
          variant="h5"
          style={{ color: "#ffffff", margin: "1rem 0", textAlign: "center" }}
        >
          Movie Listings
        </Typography>
        <Grid container spacing={3}>
          <MovieCard movies={movies} />
        </Grid>
      </Container>
    </div>
  );
}
