import React, { useState } from "react";
import {
  Typography,
  TextField,
  Button,
  Toolbar,
  Container,
} from "@mui/material";
import Navbar from "./Navbar";
import { useLocation } from "react-router-dom";
import PlaceholdePoster from "../assets/placeholder-poster.jpeg";

export default function MoviePage() {
  const location = useLocation();
  console.log(location.state.movie);

  useEffect(() => {
    axios
      .get(
        `http://api.themoviedb.org/3/discover/movie/${location.state.movie.id}?api_key=328c283cd27bd1877d9080ccb1604c91&`
      )
      .then((res) => {
        const movie = res.data.results;
        console.log("movie:", movie)
        // setMovies(movies);
      });
  }, []);

  return (
    <div>
      <Navbar />
      <Toolbar />
      <Container>
        <img
          style={{ width: "100%", height: "auto", marginTop: "1rem" }}
          src={
            location.state.movie.poster_path
              ? `http://image.tmdb.org/t/p/w500/${location.state.movie.poster_path}`
              : PlaceholdePoster
          }
          alt="movie-poster"
        />
        <Typography
          style={{ fontWeight: "bold" }}
          mt={2}
          color="primary"
          gutterBottom
          variant="body2"
        >
          Synopsis
        </Typography>
        <Typography variant="caption">
          {location.state.movie.overview}
        </Typography>
        <div
          style={{
            padding: "0.75rem",
            background: "#fcf0f5",
            display: "flex",
            justifyContent: "space-around",
            margin: "1rem 0",
          }}
        >
          <div>
            <Typography
              style={{ fontWeight: "bold" }}
              mt={2}
              color="primary"
              gutterBottom
              variant="body2"
            >
              Genre
            </Typography>
          </div>
          <div>
            <Typography
              style={{ fontWeight: "bold" }}
              mt={2}
              color="primary"
              gutterBottom
              variant="body2"
            >
              Language
            </Typography>
            <Typography variant="caption">
              {location.state.movie.original_language}
            </Typography>
          </div>
          <div>
            <Typography
              style={{ fontWeight: "bold" }}
              mt={2}
              color="primary"
              gutterBottom
              variant="body2"
            >
              Runtime
            </Typography>
          </div>
        </div>
      </Container>
    </div>
  );
}
