import React, { useState } from "react";
import {
  Typography,
  TextField,
  Button,
  Toolbar,
  Container,
} from "@mui/material";
import Navbar from "./Navbar";
import { useLocation } from "react-router-dom";
import PlaceholdePoster from "../assets/placeholder-poster.jpeg";

export default function MoviePage() {
  const location = useLocation();
  console.log(location)

  return (
    <div>
      <Navbar />
      <Toolbar />
      <Container>
        <img
          style={{ width: "100%", height: "auto", marginTop: "1rem" }}
          src={
            location.state.movie.poster_path
              ? `http://image.tmdb.org/t/p/w500/${location.state.movie.poster_path}`
              : PlaceholdePoster
          }
          alt="movie-poster"
        />
        <Typography style={{ fontWeight: "bold" }} mt={2} color="primary" gutterBottom variant="body2">Synopsis</Typography>
        {/* <Typography variant="caption">{location.state.movie}</Typography> */}
      </Container>
    </div>
  );
}
