import * as React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

export default function Filters(props) {
  const { filters, setFilters, onChangeFilters } = props

  return (
    <Box sx={{ minWidth: 120, marginTop: "1rem" }}>
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Sort Movies</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={filters}
          label="Filters"
          onChange={onChangeFilters}
        >
          <MenuItem value={"original_title.asc"}>Title (A - Z)</MenuItem>
          <MenuItem value={"original_title.desc"}>Title (Z -A)</MenuItem>
          <MenuItem value={"vote_average.asc"}>Rating (More than 5)</MenuItem>
          <MenuItem value={"vote_average.desc"}>Rating (Less than 5)</MenuItem>
        </Select>
      </FormControl>
    </Box>
  );
}
