import * as React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import NativeSelect from '@mui/material/NativeSelect';

export default function Filters(props) {
  const { filters, setFilters, onChangeFilters } = props

  return (
    <Box sx={{ minWidth: 120, marginTop: "1rem" }}>
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Sort Movies</InputLabel>
        <NativeSelect
          id="demo-simple-select"
          value={filters}
          label="Filters"
          onChange={onChangeFilters}
          defaultValue={"release_date.desc"}
        >
          <option value={"release_date.desc"}>Release Date (Z - A)</option>
          <option value={"original_title.asc"}>Title (A - Z)</option>
          <option value={"original_title.desc"}>Title (Z -A)</option>
          <option value={"vote_average.asc"}>Rating (More than 5)</option>
          <option value={"vote_average.desc"}>Rating (Less than 5)</option>
        </NativeSelect>
      </FormControl>
    </Box>
  );
}
