import * as React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

export default function Filters() {
  const [age, setAge] = React.useState('');

  const handleChange = (event) => {
    setAge(event.target.value);
  };

  return (
    <Box sx={{ minWidth: 120 }}>
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Sort Movies</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={age}
          label="Filters"
          onChange={handleChange}
        >
          <MenuItem value={10}>Title (A - Z)</MenuItem>
          <MenuItem value={20}>Title (Z -A)</MenuItem>
          <MenuItem value={30}>Rating (More than 5)</MenuItem>
          <MenuItem value={40}>Rating (Less than 5)</MenuItem>
        </Select>
      </FormControl>
    </Box>
  );
}
